import Header from "../../components/Header";
import Footer from "../../components/Footer";
import React from "react";
import CartProductUnit from "../../components/CartProductUnit";
import CartFooter from "../../components/CartFooter";
import axios from "axios";
import { actions } from "../../store";
import { connect } from "unistore/react";

class Cart extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      total_price: 0,
      cart_data: []
    };
  }

  componentDidMount = () => {
    const self = this;
    axios
      .get(this.props.host + "order?status=10", {
        headers: {
          Authorization: "Bearer " + String(localStorage.getItem("user_token"))
        }
      })
      .then(response => {
        // console.log(response);
        // self.props.setCart(response.data);
        self.setState({ cart_data: response.data });
      })
      .catch(error => {
        console.log(error);
      });
  };

  render() {
    console.log(this.state.cart_data[0]);
    return (
      <div>
        <Header />
        <div className="container" style={{ minHeight: "100vh" }}>
          <br />

