import axios from "axios";
import { actions } from "../../store";
import { connect } from "unistore/react";


class Cart extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      total_price: 0,
      list: []
    };
  }

  componentDidMount = async () => {
    const self = this;
    // await axios
    //   .get("http://13.229.151.2/product/all")
    //   .then(function (response) {
    //     // console.log(response.data)
    //     self.props.setProduct(response.data);
    //   })
    //   .catch(function (error) { });

    axios
      .get("http://localhost:8888/order?status=1",
        {
          headers: {
            Authorization: "Bearer " + String(localStorage.getItem('user_token'))
          }
        })
      .then(response => {
        // console.log(response.data);
        self.setState({list: response.data});
      })
      .catch(error => {
        console.log(error);
      });


    // var total_price = 0
    // console.log(self.props.cartList)
    // await this.props.cartList.data.map((item, index) => {
    //   total_price += item.price
    //   console.log("apa")
    // })
    // await self.setState({ total_price: total_price })
    // console.log(total_price)
    // console.log(self.state.total_price)
  }

  render() {
    console.log(this.state.list)
    return (
      <div>
        <Header />
        <div className="container" style={{ minHeight: "100vh" }}>
          <br />

          <h3 className="card-title">Keranjang Belanja</h3>
          {this.props.cartList.map((item, index) => {
            item.data.map( items => {          
            return <CartProductUnit price={items.price} qty={items.qty} name={items.product_name} img_src={items.foto_product}/>// onClick={item.cart_id} />
            })})}

          {/* <CartFooter total_price={this.state.list[0].total_price} /> */}
          <br />
        </div>
        <Footer />
      </div>
    );
  }
}

export default connect("cart, cartList, cartTotalPrice, product", actions)(Cart);
