import HeaderSeller from "../../components_seller/HeaderSeller";
import FooterSeller from "../../components_seller/FooterSeller";


import React from "react";
import axios from "axios";
import { connect } from "unistore/react";
import { actions } from "../../store";
import { Redirect, Link } from 'react-router-dom'

class SigninSeller extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      password: ""
    };
  }

  setUsername = async event => {
    event.preventDefault();
    await this.setState({ name: event.target.value });
  };

  setPassword = async event => {
    event.preventDefault();
    await this.setState({ password: event.target.value });
  };

  doLogin = async e => {
    e.preventDefault();
    const self = this;
    await axios
      .post("http://13.229.151.2/login/seller", {
        name: self.state.name,
        password: self.state.password
      })
      .then(response => {
        localStorage.setItem('seller_logged_in', true)
        localStorage.setItem('seller_token', response.data.token)
        console.log("Anda Berhasil Login!")
        window.location.reload()
      })
      .catch(error => {
        console.log(error);
        alert('Salah Username atau Password! Coba Ulangi')
      });
  };

  render() {
    if (localStorage.getItem('seller_logged_in') !== 'true') {
      return (
        <div>
          <HeaderSeller />

          <div className="container" style={{ minHeight: "100vh" }}>
            <div className="row justify-content-center">
              <div className="col-md-6 text-center">
                <form class="form-signin">
                  <br />
                  <br />
                  <br />
                  <img
                    class="mb-4"
                    // src={blackLogo}
                    alt="Ba-ik"
                  />
                  <h1 class="h3 mb-3 font-weight-normal">Please Sign In</h1>
                  <label for="inputUsername" class="sr-only">
                    Username
                  </label>
                  <input
                    type="text"
                    id="inputUsername"
                    class="form-control"
                    placeholder="Username"
                    onChange={event => this.setUsername(event)}
                  />
                  <br />
                  <label for="inputPassword" class="sr-only">
                    Password
                  </label>
                  <input
                    type="password"
                    id="inputPassword"
                    class="form-control"
                    placeholder="Password"
                    onChange={event => this.setPassword(event)}
                  />
                  <br />
                  <button class="btn btn-lg btn-primary btn-block" type="submit" onClick={e => this.doLogin(e)}>
                    Sign in
                  </button>
                  <p class="mt-5 mb-3 text-muted">© 2019</p>
                </form>
              </div>
            </div>
          </div>
          <FooterSeller />
        </div>
      );
    } else {
      return (
        <div>
          <HeaderSeller />
          <div className="container" style={{ minHeight: "100vh" }}>
            <div className="row">
              <div className="col-12 text-center">
                <br />
                <h1> Selamat Datang di Halaman Seller!</h1>
                <h4>Silakan pilih menu:</h4>
                <h6>* Bisa juga gunakan menu header diatas</h6>
                <Link to="/seller/transaction">
                  <button type="button" class="btn btn-primary btn-lg btn-block">Transaction</button>
                </Link>
                <Link to="/seller/category">
                  <button type="button" class="btn btn-secondary btn-lg btn-block">Category</button>
                </Link>
                <Link to="seller/product">
                  <button type="button" class="btn btn-success btn-lg btn-block">Product</button>
                </Link>

              </div>
            </div>
          </div>
          <FooterSeller />
        </div>
      )
    }

  }
}

export default connect('', actions)(SigninSeller);
